parameters.jsCode:
// Process incoming webhook data from Cloudflare Worker
const webhookData = $json;
const currentTime = new Date().toISOString();

// Extract the status data sent from the Worker
const statusData = {
  timestamp: webhookData.timestamp || currentTime,
  trigger_reason: webhookData.trigger_reason || 'status_update',
  summary: webhookData.summary || {},
  servers: webhookData.servers || {},
  changed_servers: webhookData.changed_servers || [],
  system_status: webhookData.system_status || 'unknown'
};

console.log('üîÑ Received NDI status update via webhook');
console.log(`üìä Summary: ${JSON.stringify(statusData.summary)}`);
console.log(`üéØ Trigger reason: ${statusData.trigger_reason}`);
console.log(`üîÑ Changed servers: ${statusData.changed_servers.length}`);

// Determine if this update requires immediate attention
const requiresAlert = 
  statusData.summary.offline_servers > 0 ||
  statusData.system_status === 'degraded' ||
  statusData.system_status === 'critical' ||
  statusData.trigger_reason === 'server_status_change' ||
  statusData.trigger_reason === 'system_degraded';

return {
  ...statusData,
  requires_alert: requiresAlert,
  processing_timestamp: currentTime
};

// Create alert from webhook status data
const webhookData = $json;
const timestamp = webhookData.timestamp;
const summary = webhookData.summary;
const servers = webhookData.servers;
const changedServers = webhookData.changed_servers || [];

// Determine severity based on impact
let severity = 'LOW';
if (summary.offline_servers > 0) severity = 'MEDIUM';
if (summary.offline_servers > (summary.total_servers / 2)) severity = 'HIGH';
if (webhookData.system_status === 'critical') severity = 'HIGH';

// Determine alert type from trigger reason and status
let alertType = 'NDI_STATUS_CHANGE';
switch (webhookData.trigger_reason) {
  case 'server_offline':
    alertType = 'NDI_SERVER_OFFLINE';
    break;
  case 'server_online':
    alertType = 'NDI_SERVER_RECOVERED';
    severity = 'LOW'; // Recovery is good news
    break;
  case 'system_degraded':
    alertType = 'NDI_SYSTEM_DEGRADED';
    break;
  case 'health_score_drop':
    alertType = 'NDI_PERFORMANCE_DEGRADED';
    break;
  default:
    alertType = 'NDI_STATUS_UPDATE';
}

const alert = {
  timestamp: timestamp,
  alert_type: alertType,
  severity: severity,
  trigger_reason: webhookData.trigger_reason,
  changed_servers: changedServers,

  summary: {
    title: `üö® NDI Alert: ${alertType.replace('NDI_', '').replace('_', ' ')}`,
    total_servers: summary.total_servers || 0,
    online_servers: summary.online_servers || 0,
    offline_servers: summary.offline_servers || 0,
    uptime_percentage: summary.uptime_percentage || 0,
    system_status: webhookData.system_status
  },

  affected_servers: [],
  healthy_servers: [],
  recently_changed: [],
  formatted_message: ''
};

// Process server details
for (const [serverId, server] of Object.entries(servers)) {
  const serverInfo = {
    id: serverId,
    name: server.server_name || serverId,
    health_score: server.health_score || 0,
    last_seen: server.last_seen || 'Unknown',
    source_count: server.source_count || 0
  };

  // Check if this server recently changed status
  const recentlyChanged = changedServers.some(changed => 
    changed.server_id === serverId
  );

  if (recentlyChanged) {
    const changeInfo = changedServers.find(c => c.server_id === serverId);
    serverInfo.change_type = changeInfo.change_type;
    serverInfo.previous_status = changeInfo.previous_status;
    serverInfo.new_status = changeInfo.new_status;
    alert.recently_changed.push(serverInfo);
  }

  if (server.status === 'offline' || !server.alive) {
    alert.affected_servers.push({
      ...serverInfo,
      issue: server.error || 'Server offline or not responding'
    });
  } else {
    alert.healthy_servers.push(serverInfo);
  }
}

// Create formatted message
let message = `üö® NDI INFRASTRUCTURE EVENT\n\n`;
message += `Event: ${alertType}\n`;
message += `Severity: ${severity}\n`;
message += `Time: ${timestamp}\n`;
message += `Trigger: ${webhookData.trigger_reason}\n`;
message += `System Status: ${webhookData.system_status?.toUpperCase()}\n\n`;

if (alert.recently_changed.length > 0) {
  message += `=== RECENT CHANGES ===\n`;
  for (const server of alert.recently_changed) {
    const icon = server.new_status === 'online' ? '‚úÖ' : '‚ùå';
    message += `${icon} ${server.name}\n`;
    message += `   Changed: ${server.previous_status} ‚Üí ${server.new_status}\n`;
    message += `   Health: ${server.health_score}%\n\n`;
  }
}

if (alert.affected_servers.length > 0) {
  message += `=== AFFECTED SERVERS (${alert.affected_servers.length}) ===\n`;
  for (const server of alert.affected_servers) {
    message += `‚ùå ${server.name}\n`;
    message += `   Issue: ${server.issue}\n`;
    message += `   Last seen: ${server.last_seen}\n\n`;
  }
}

if (alert.healthy_servers.length > 0) {
  message += `=== HEALTHY SERVERS (${alert.healthy_servers.length}) ===\n`;
  for (const server of alert.healthy_servers) {
    message += `‚úÖ ${server.name} (${server.health_score}% health)\n`;
  }
}

message += `\n---\nReal-time NDI Monitoring System\n`;
message += `Dashboard: https://confluence.your-domain.com/ndi-status`;

alert.formatted_message = message;

console.log(`üî• Event-driven alert: ${alertType} (${severity})`);
console.log(`üìà Changed servers: ${alert.recently_changed.length}`);
console.log(`‚ö†Ô∏è Affected servers: ${alert.affected_servers.length}`);

return alert;

// Log routine status update (no alert needed)
const webhookData = $json;
const timestamp = webhookData.timestamp;

const statusLog = {
  timestamp: timestamp,
  status: 'routine_update',
  trigger_reason: webhookData.trigger_reason,
  total_servers: webhookData.summary.total_servers || 0,
  online_servers: webhookData.summary.online_servers || 0,
  system_status: webhookData.system_status,
  changed_servers: webhookData.changed_servers?.length || 0,
  message: 'Routine NDI status update - no issues detected'
};

console.log('üìä Routine NDI status update received');
console.log(`üîÑ Trigger: ${webhookData.trigger_reason}`);
console.log(`üìà Servers: ${statusLog.online_servers}/${statusLog.total_servers} online`);
console.log(`üü¢ System status: ${statusLog.system_status}`);

// Only update Confluence for significant routine updates
const shouldUpdateConfluence = 
  webhookData.trigger_reason === 'periodic_summary' ||
  statusLog.changed_servers > 0;

return {
  ...statusLog,
  confluence_update: shouldUpdateConfluence
};

// Prepare Confluence update from webhook data
const webhookData = $json;
const isAlert = webhookData.alert_type !== undefined;
const timestamp = webhookData.timestamp;

// Use alert data if available, otherwise use routine update data
const summary = isAlert ? webhookData.summary : {
  total_servers: webhookData.total_servers,
  online_servers: webhookData.online_servers,
  offline_servers: webhookData.total_servers - webhookData.online_servers,
  system_status: webhookData.system_status
};

const systemStatus = isAlert ? webhookData.system_status : webhookData.system_status;
const severity = isAlert ? webhookData.severity : 'NORMAL';

// Create Confluence page content
let confluenceContent = `# NDI Infrastructure Status\n\n`;
confluenceContent += `**Last Updated:** ${timestamp}\n`;
confluenceContent += `**System Status:** ${systemStatus.toUpperCase()}\n`;
confluenceContent += `**Alert Level:** ${severity}\n\n`;

if (isAlert && webhookData.recently_changed?.length > 0) {
  confluenceContent += `## üîÑ Recent Changes\n\n`;
  for (const server of webhookData.recently_changed) {
    const icon = server.new_status === 'online' ? '‚úÖ' : '‚ùå';
    confluenceContent += `${icon} **${server.name}**\n`;
    confluenceContent += `- Status changed: ${server.previous_status} ‚Üí ${server.new_status}\n`;
    confluenceContent += `- Health Score: ${server.health_score}%\n\n`;
  }
}

// System overview table
confluenceContent += `## System Overview\n\n`;
confluenceContent += `| Metric | Value |\n`;
confluenceContent += `|--------|--------|\n`;
confluenceContent += `| Total Servers | ${summary.total_servers} |\n`;
confluenceContent += `| Online Servers | ${summary.online_servers} |\n`;
confluenceContent += `| Offline Servers | ${summary.offline_servers || 0} |\n`;
confluenceContent += `| System Status | ${systemStatus.toUpperCase()} |\n`;
confluenceContent += `| Alert Level | ${severity} |\n`;
confluenceContent += `| Last Event | ${webhookData.trigger_reason || 'routine_update'} |\n\n`;

// Add server details if available
if (isAlert) {
  if (webhookData.affected_servers?.length > 0) {
    confluenceContent += `## ‚ö†Ô∏è Servers Requiring Attention\n\n`;
    for (const server of webhookData.affected_servers) {
      confluenceContent += `### ${server.name}\n`;
      confluenceContent += `- **Status:** ‚ùå OFFLINE\n`;
      confluenceContent += `- **Issue:** ${server.issue}\n`;
      confluenceContent += `- **Last Seen:** ${server.last_seen}\n\n`;
    }
  }

  if (webhookData.healthy_servers?.length > 0) {
    confluenceContent += `## ‚úÖ Healthy Servers\n\n`;
    for (const server of webhookData.healthy_servers) {
      confluenceContent += `### ${server.name}\n`;
      confluenceContent += `- **Status:** ‚úÖ ONLINE\n`;
      confluenceContent += `- **Health Score:** ${server.health_score}%\n`;
      confluenceContent += `- **Active Sources:** ${server.source_count}\n\n`;
    }
  }
}

// Technical resources
confluenceContent += `## Technical Resources\n\n`;
confluenceContent += `- [API Documentation](https://github.com/your-org/ndi-monitoring-system)\n`;
confluenceContent += `- [Deployment Guide](https://github.com/your-org/ndi-monitoring-system/blob/main/docs/deployment-guide.md)\n`;
confluenceContent += `- [Troubleshooting Guide](https://github.com/your-org/ndi-monitoring-system/blob/main/docs/troubleshooting.md)\n\n`;

confluenceContent += `---\n*Real-time updates via NDI Event Monitoring*`;

const confluenceUpdate = {
  page_id: "YOUR_CONFLUENCE_PAGE_ID",
  page_title: "NDI Infrastructure Status",
  content: confluenceContent,
  update_type: severity === 'HIGH' ? 'major_update' : 'minor_update',
  version_message: `Real-time update: ${webhookData.trigger_reason || 'status_change'} at ${timestamp}`,
  event_driven: true
};

console.log('üìù Prepared real-time Confluence update');
console.log(`üéØ Update type: ${confluenceUpdate.update_type}`);
console.log(`üîÑ Trigger: ${webhookData.trigger_reason}`);

return confluenceUpdate;

parameters.jsonBody:
={
  "text": "üö® NDI Real-time Alert",
  "attachments": [
    {
      "color": "{{ $json.severity === 'HIGH' ? 'danger' : ($json.severity === 'MEDIUM' ? 'warning' : 'good') }}",
      "fields": [
        {
          "title": "Event Type",
          "value": "{{ $json.alert_type }}",
          "short": true
        },
        {
          "title": "Severity",
          "value": "{{ $json.severity }}",
          "short": true
        },
        {
          "title": "Trigger",
          "value": "{{ $json.trigger_reason }}",
          "short": true
        },
        {
          "title": "Changed Servers",
          "value": "{{ $json.recently_changed.length }}",
          "short": true
        }
      ],
      "text": "{{ $json.formatted_message }}",
      "footer": "NDI Real-time Monitoring",
      "ts": {{ Math.floor(Date.now() / 1000) }}
    }
  ]
}
